import React, { useState } from 'react';
import './styles/AdminProfile.css';

function AdminProfile() {
  const [activeTab, setActiveTab] = useState('users'); // Estado para la pestaña activa
  const [userData, setUserData] = useState({ username: '', password: '', role: '' });
  const [productData, setProductData] = useState({ name: '', price: '' });

  const handleUserSubmit = (e) => {
    e.preventDefault();
    console.log('Datos del nuevo usuario:', userData);
    setUserData({ username: '', password: '', role: '' });
  };

  const handleProductSubmit = (e) => {
    e.preventDefault();
    console.log('Datos del nuevo producto:', productData);
    setProductData({ name: '', price: '' });
  };

  return (
    <div className="container">
      <div className="tabs">
        <button className={activeTab === 'users' ? 'active' : ''} onClick={() => setActiveTab('users')}>Usuarios</button>
        <button className={activeTab === 'products' ? 'active' : ''} onClick={() => setActiveTab('products')}>Productos</button>
        <button className={activeTab === 'sales' ? 'active' : ''} onClick={() => setActiveTab('sales')}>Ventas</button>
      </div>

      {activeTab === 'users' && (
        <div className="form-container">
          <h3>Crear nuevo usuario</h3>
          <form onSubmit={handleUserSubmit}>
            <input
              type="text"
              placeholder="Nombre de usuario"
              value={userData.username}
              onChange={(e) => setUserData({ ...userData, username: e.target.value })}
            />
            <input
              type="password"
              placeholder="Contraseña"
              value={userData.password}
              onChange={(e) => setUserData({ ...userData, password: e.target.value })}
            />
            <input
              type="text"
              placeholder="Rol"
              value={userData.role}
              onChange={(e) => setUserData({ ...userData, role: e.target.value })}
            />
            <button type="submit">Guardar</button>
          </form>
        </div>
      )}

      {activeTab === 'products' && (
        <div className="form-container">
          <h3>Crear nuevo producto</h3>
          <form onSubmit={handleProductSubmit}>
            <input
              type="text"
              placeholder="Nombre del producto"
              value={productData.name}
              onChange={(e) => setProductData({ ...productData, name: e.target.value })}
            />
            <input
              type="text"
              placeholder="Precio"
              value={productData.price}
              onChange={(e) => setProductData({ ...productData, price: e.target.value })}
            />
            <button type="submit">Guardar</button>
          </form>
        </div>
      )}

      {activeTab === 'sales' && (
        <div className="table-container">
          <h3>Tabla de ventas</h3>
          {/* Aquí puedes mostrar la tabla de ventas */}
        </div>
      )}
    </div>
  );
}

export default AdminProfile;
