import React, { useState } from 'react';
import './styles/Admin.css';

function AdminProfile() {
  // Estado para los datos del nuevo usuario
  const [userData, setUserData] = useState({ username: '', password: '', role: '' });

  // Estado para los datos del nuevo producto
  const [productData, setProductData] = useState({ name: '', price: '' });

  // Estado para mostrar la pestaña activa
  const [activeTab, setActiveTab] = useState('user');

  // Función para manejar el envío del formulario de creación de usuario
  const handleUserSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes enviar userData al servidor para procesarlo
    console.log('Datos del nuevo usuario:', userData);
    // Limpia los campos después de enviar el formulario
    setUserData({ username: '', password: '', role: '' });
  };

  // Función para manejar el envío del formulario de creación de producto
  const handleProductSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes enviar productData al servidor para procesarlo
    console.log('Datos del nuevo producto:', productData);
    // Limpia los campos después de enviar el formulario
    setProductData({ name: '', price: '' });
  };

  return (
    <div className="container">
      <h2>Admin Profile</h2>
      
      {/* Pestañas */}
      <div className="tabs">
        <button onClick={() => setActiveTab('user')} className={activeTab === 'user' ? 'active' : ''}>Usuario</button>
        <button onClick={() => setActiveTab('product')} className={activeTab === 'product' ? 'active' : ''}>Producto</button>
        <button onClick={() => setActiveTab('table')} className={activeTab === 'table' ? 'active' : ''}>Tabla</button>
      </div>

      {/* Formulario para la creación de usuario */}
      {activeTab === 'user' && (
        <div className="form-container">
          <h3>Crear nuevo usuario</h3>
          <form onSubmit={handleUserSubmit}>
            {/* Inputs para el formulario de usuario */}
          </form>
        </div>
      )}

      {/* Formulario para la creación de producto */}
      {activeTab === 'product' && (
        <div className="form-container">
          <h3>Crear nuevo producto</h3>
          <form onSubmit={handleProductSubmit}>
            {/* Inputs para el formulario de producto */}
          </form>
        </div>
      )}

      {/* Tabla de ventas */}
      {activeTab === 'table' && (
        <div className="table-container">
          <h3>Tabla de ventas</h3>
          {/* Aquí puedes mostrar la tabla de ventas */}
        </div>
      )}
    </div>
  );
}

export default AdminProfile;
