// App.js
import React, { useState } from 'react';
import './App.css';
import logo from './logo.png';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import AdminProfile from './AdminProfile'; // Importa el componente AdminProfile
import { useNavigate } from 'react-router-dom';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  const navigate = useNavigate();

const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const response = await fetch('http://localhost:5000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });
    const data = await response.json();
    setMessage(data.message);
    if (data.user) {
      navigate('/admin-profile'); // Redirige al usuario a la p치gina de AdminProfile
    }
  } catch (error) {
    console.error('Error:', error);
  }
};


  return (
    <Router>
      <div className="App">
        <div className="form-container">
          <div className="logo">
            <img src={logo} alt="Logo" />
          </div>
          <h1>LOGIN</h1>
          <div>
            <input type="text" placeholder="Nombre de usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
          </div>
          <div>
            <input type="password" placeholder="Contrase침a" value={password} onChange={(e) => setPassword(e.target.value)} />
          </div>
          <button onClick={handleSubmit}>Iniciar sesi칩n</button>
          <p className="message">{message}</p>
        </div>
      </div>
      <Route path="/admin-profile">
        {loggedIn ? <AdminProfile /> : <Redirect to="/" />}
      </Route>
    </Router>
  );
}

export default App;
