import React, { useState } from 'react';
import './styles/Mesero.css';

const Mesero = () => {
    const [mesa, setMesa] = useState('');
    const [productos, setProductos] = useState([]);
    const [total, setTotal] = useState(0);

    const handleAgregarProducto = () => {
        const nuevoProducto = {
            nombre: '',
            cantidad: 0,
            precioUnitario: 0
        };
        setProductos([...productos, nuevoProducto]);
    };

    const handleCambiarProducto = (index, e) => {
        const { name, value } = e.target;
        const nuevosProductos = [...productos];
        nuevosProductos[index][name] = value;
        setProductos(nuevosProductos);
    };

    const handleEliminarProducto = (index) => {
        const nuevosProductos = [...productos];
        nuevosProductos.splice(index, 1);
        setProductos(nuevosProductos);
    };

    const handleCalcularTotal = () => {
        let totalCalculado = 0;
        productos.forEach(producto => {
            totalCalculado += producto.cantidad * producto.precioUnitario;
        });
        setTotal(totalCalculado);
    };

    const handleRealizarPedido = () => {
        // Aquí puedes enviar los datos del pedido al backend o realizar cualquier otra acción necesaria
        console.log('Pedido realizado:', { mesa, productos, total });
        // Después de realizar el pedido, puedes limpiar los estados de mesa, productos y total
        setMesa('');
        setProductos([]);
        setTotal(0);  
    };

    return (
        <div className="mesero-container">
            <h2>Realizar Pedido</h2>
            <form className="mesero-form">
                <div>
                    <label htmlFor="mesa">Mesa:</label>
                    <input type="text" id="mesa" name="mesa" value={mesa} onChange={(e) => setMesa(e.target.value)} />
                </div>
                {productos.map((producto, index) => (
                    <div key={index}>
                        <label htmlFor={`producto${index}`}>Producto:</label>
                        <input type="text" id={`producto${index}`} name="nombre" value={producto.nombre} onChange={(e) => handleCambiarProducto(index, e)} />
                        <label htmlFor={`cantidad${index}`}>Cantidad:</label>
                        <input type="number" id={`cantidad${index}`} name="cantidad" value={producto.cantidad} onChange={(e) => handleCambiarProducto(index, e)} />
                        <label htmlFor={`precio${index}`}>Precio Unitario:</label>
                        <input type="number" id={`precio${index}`} name="precioUnitario" value={producto.precioUnitario} onChange={(e) => handleCambiarProducto(index, e)} />
                        
                    </div>
                ))}
                
                <div>Total: {total}</div>
            </form>
        </div>
    );
}

export default Mesero;
