import React, { useState, useEffect } from 'react';
import './styles/EditarUsuario.css';

function EditUserForm({ user, onSave }) {
    const [editedUser, setEditedUser] = useState({ ...user });
    const [updatedUserData, setUpdatedUserData] = useState({});

    useEffect(() => {
        // Actualizar el estado cuando cambie el usuario seleccionado
        setEditedUser({ ...user });
    }, [user]);

    const handleChange = (e) => {
        const { name, value } = e.target;
  setUpdatedUserData(prevData => ({
    ...prevData,
    [name]: value
  }));
};

    const handleSubmit = async (username) => {
        username.preventDefault();
        try {
            const response = await fetch(`http://localhost:5000/users/${username}`, {
              method: 'PUT',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify(updatedUserData),
            });
        
            if (response.ok) {
              // La solicitud fue exitosa, puedes mostrar un mensaje de éxito o redirigir al usuario a otra página
            } else {
              // La solicitud falló, maneja el error como prefieras
            }
          } catch (error) {
            console.error('Error:', error);
          }
    
        onSave(editedUser);
    };

    return (
        <div className="edit-user-form">
            <h2>Editar Usuario</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre de usuario:</label>
                    <input
                        type="text"
                        name="username"
                        value={editedUser.username}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Contraseña:</label>
                    <input
                        type="password"
                        name="password"
                        value={editedUser.password}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Rol:</label>
                    <select
                        name="role"
                        value={editedUser.role}
                        onChange={handleChange}
                    >
                        <option value="admin">Admin</option>
                        <option value="mesero">Mesero</option>
                        <option value="cocina">Cocina</option>
                    </select>
                </div>
                <button type="submit">Guardar cambios</button>
            </form>
        </div>
    );
}

export default EditUserForm;
