import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/Admin.css';

function VentasProfile() {
  const [ventas, setVentas] = useState(null);
  const goTo = useNavigate();

  useEffect(() => {
    async function fetchVentas() {
      try {
        const response = await fetch('http://localhost:5000/ventas');
        if (response.ok) {
          const data = await response.json();
          setVentas(data);
        } else {
          console.error('Error al cargar las ventas');
        }
      } catch (error) {
        console.error('Error al cargar las ventas:', error);
      }
    }

    if (activeTab === 'sales') {
      fetchVentas();
    }
  }, [activeTab]);

  

  const handleLogout = () => {
    goTo('/');
  }
 

  const ListaVentas = () => {
    goTo('/listaventas');

  }


  return (
    <div className="container">
      <div className="tabs">
        
        <button className={activeTab === 'sales' ? 'active' : ''} onClick={() => setActiveTab('sales')}>
          Tabla de ventas
        </button>
        <button onClick={ListaVentas}>Tabla de Ventas </button>
        <button onClick={ListaProductos}>Lista Productos</button>
        <button onClick={ListaUsuario}>Lista Usuarios</button>
      </div>

        <div className="form-container">
        {activeTab === 'sales'  && (
          <div className="table-container">
            <h2>Tabla de Ventas</h2>
            <table>
              <thead>
                <tr>
                  <th>Mesero</th>
                  <th>Productos</th>
                  <th>Total</th>
                </tr>
              </thead>
              <tbody>
                {ventas.map((venta, index) => (
                  <tr key={index}>
                    <td>{venta.mesero}</td>
                    <td>{venta.productos.map((producto) => producto.nombre).join(', ')}</td>
                    <td>${venta.total}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

      </div>
    </div>
  );
}
export default VentasProfile;
