import React, { useState, useEffect } from 'react';
import './styles/Cocina.css';

function Cocina() {
  const [pedidos, setPedidos] = useState([]);

  useEffect(() => {
    async function fetchPedidos() {
      try {
        const response = await fetch('http://localhost:5000/pedidos');
        if (response.ok) {
          const data = await response.json();
          setPedidos(data);
        } else {
          console.error('Error al cargar los pedidos');
        }
      } catch (error) {
        console.error('Error al cargar los pedidos:', error);
      }
    }

    fetchPedidos();
  }, []);

  const handlePedidoListo = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/pedidos/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ estadoPedido: 'listo' }),
      });

      if (response.ok) {
        // Actualizar la lista de pedidos después de cambiar el estado
        const updatedPedidos = pedidos.map(pedido => {
          if (pedido.id === id) {
            return { ...pedido, estadoPedido: 'listo' };
          }
          return pedido;
        });
        setPedidos(updatedPedidos);
      } else {
        console.error('Error al marcar el pedido como listo');
      }
    } catch (error) {
      console.error('Error al marcar el pedido como listo:', error);
    }
  };

  return (
    <div className="cocina-container">
      <h2>Lista de Pedidos en Cocina</h2>
      <div className="pedidos-list">
        {pedidos.map(pedido => (
          <div key={pedido.id} className="pedido-item">
            <p>{pedido.mesa}</p>
            <p>{pedido.mesero}</p>
            <p>{pedido.estadoPedido}</p>
            <button onClick={() => handlePedidoListo(pedido.id)}>Marcar como listo</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Cocina;
