import React, { useState, useEffect } from 'react';

function AdminProfile() {
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Obtener la lista de usuarios desde el backend
    fetch('http://localhost:5000/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const handleEditUser = (userId) => {
    // Implementa la l贸gica para editar el usuario con el ID proporcionado
    console.log('Editar usuario:', userId);
  };

  const handleDeleteUser = (userId) => {
    // Implementa la l贸gica para eliminar el usuario con el ID proporcionado
    console.log('Eliminar usuario:', userId);
  };
  
  return (
    <div>
      {/* Mostrar la lista de usuarios */}
      <h2>Usuarios</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.username} - {user.role} 
            <button onClick={() => handleEditUser(user.id)}>Editar</button>
            <button onClick={() => handleDeleteUser(user.id)}>Eliminar</button>
          </li>
        ))}
      </ul>

      {/* Mostrar la lista de productos */}
      <h2>Productos</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            {product.name} - {product.price} 
            <button onClick={() => handleEditProduct(product.id)}>Editar</button>
            <button onClick={() => handleDeleteProduct(product.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminProfile;
