// Por ejemplo, puedes usar useEffect para cargar la lista de usuarios y productos cuando el componente se monta

import React, { useState, useEffect } from 'react';

function AdminProfile() {
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Obtener la lista de usuarios desde el backend
    fetch('http://localhost:5000/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));

    // Obtener la lista de productos desde el backend
    fetch('http://localhost:5000/products')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  return (
    <div>
      {/* Mostrar la lista de usuarios */}
      <h2>Usuarios</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>{user.username} - {user.role}</li>
          // Agrega botones o enlaces para modificar o eliminar usuarios
        ))}
      </ul>

      {/* Mostrar la lista de productos */}
      <h2>Productos</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>{product.name} - {product.price}</li>
          // Agrega botones o enlaces para modificar o eliminar productos
        ))}
      </ul>
    </div>
  );
}

export default AdminProfile;
