import React, { useState } from 'react';
import './styles/Mesero.css';

function Mesero() {
    // Estado para los detalles del nuevo pedido
    const [producto, setProducto] = useState('');
    const [cantidad, setCantidad] = useState('');
    const [mesa, setMesa] = useState('');

    // Estado para almacenar los pedidos realizados
    const [pedidosRealizados, setPedidosRealizados] = useState([]);

    // Función para manejar el envío de un nuevo pedido
    const handleSubmitPedido = (e) => {
        e.preventDefault();
        // Validar que los campos no estén vacíos
        if (!producto || !cantidad || !mesa) {
            alert('Por favor, complete todos los campos');
            return;
        }
        // Agregar el nuevo pedido a la lista de pedidos realizados
        const nuevoPedido = {
            producto,
            cantidad,
            mesa
        };
        setPedidosRealizados([...pedidosRealizados, nuevoPedido]);
        // Restablecer los campos del formulario
        setProducto('');
        setCantidad('');
        setMesa('');
    };

    return (
      <div className="mesero-container">
          <h2>Realizar Pedido</h2>
          <form className="mesero-form">
              <div>
                  <label htmlFor="mesa">Mesa:</label>
                  <input type="text" id="mesa" name="mesa" />
              </div>
              <div>
                  <label htmlFor="producto">Producto:</label>
                  <input type="text" id="producto" name="producto" />
              </div>
              <div>
                  <label htmlFor="cantidad">Cantidad:</label>
                  <input type="text" id="cantidad" name="cantidad" />
              </div>
              <button type="submit">Realizar Pedido</button>
          </form>

          <h2>Pedidos Realizados</h2>
          <ul className="mesero-pedidos">
              {/* Aquí se mostrarán los pedidos realizados */}
              <li>Pedido 1</li>
              <li>Pedido 2</li>
              <li>Pedido 3</li>
          </ul>
      </div>
  );
}

export default Mesero;
