import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/ListaUsuarios.css';

function AdminProfile() {
  
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/products')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);


  const handleEditProduct = (productId) => {
    // Implementa la l贸gica para editar el producto con el ID proporcionado
    console.log('Editar producto:', productId);
  };

  const handleDeleteProduct = (productId) => {
    // Implementa la l贸gica para eliminar el producto con el ID proporcionado
    console.log('Eliminar producto:', productId);
  };

  const handleVolver = () => {
    goTo('/administrador');
};

  return (
    <div>
      {/* Mostrar la lista de usuarios */}
      <h2>Usuarios</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.username} - {user.role} 
            <button onClick={() => handleEditUser(user.id)}>Editar</button>
            <button onClick={() => handleDeleteUser(user.id)}>Eliminar</button>
          </li>
        ))}
      </ul>

      {/* Mostrar la lista de productos */}
      <h2>Productos</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            {product.name} - {product.price} 
            <button onClick={() => handleEditProduct(product.id)}>Editar</button>
            <button onClick={() => handleDeleteProduct(product.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminProfile;
