import React, { useState } from 'react';



function Mesero() {
    const [pedidosMesero] = useState(pedidos); // Estado para almacenar los pedidos del mesero
    const [nuevoPedido, setNuevoPedido] = useState({
        mesa: '',
        pedido: []
    }); // Estado para almacenar los datos del nuevo pedido

    // Función para manejar cambios en el formulario de nuevo pedido
    const handleChange = (e) => {
        const { name, value } = e.target;
        setNuevoPedido(prevPedido => ({
            ...prevPedido,
            [name]: value
        }));
    };


    return (
        <div className="mesero-container">
            <h2>Perfil del Mesero</h2>
            <h3>Pedidos del Mesero</h3>
            <ul>
                {pedidosMesero.map((pedido, index) => (
                    <li key={index}>
                        <p>Mesa: {pedido.mesa}</p>
                        <p>Estado: {pedido.estado}</p>
                        <p>Pedido:</p>
                        <ul>
                            {pedido.pedido.map((item, i) => (
                                <li key={i}>{item.cantidad}x {item.producto} - ${item.precio}</li>
                            ))}
                        </ul>
                    </li>
                ))}
            </ul>
            <h3>Realizar Nuevo Pedido</h3>
            <form onSubmit={handleSubmit}>
                <label htmlFor="mesa">Mesa:</label>
                <input type="text" id="mesa" name="mesa" value={nuevoPedido.mesa} onChange={handleChange} />
                <label htmlFor="productos">Productos:</label>
                <select id="productos" name="productos" onChange={handleChange}>
                    <option value="">Seleccione un producto</option>
                    {/* Aquí podrías iterar sobre una lista de productos disponibles y mostrarlos como opciones */}
                    <option value="hamburguesa">Hamburguesa</option>
                    <option value="pizza">Pizza</option>
                    <option value="malteada">Malteada</option>
                    {/* Agrega más opciones según tus productos disponibles */}
                </select>
                <label htmlFor="cantidad">Cantidad:</label>
                <input type="number" id="cantidad" name="cantidad" value={nuevoPedido.cantidad} onChange={handleChange} />
                <button type="button" onClick={() => agregarProducto(nuevoPedido.producto, nuevoPedido.cantidad, 10000)}>Agregar Producto</button>
                {/* Aquí podrías mostrar los productos seleccionados para confirmación antes de enviar el pedido */}
                <button type="submit">Realizar Pedido</button>
            </form>
        </div>
    );
}

export default Mesero;
