import React, { useState, useEffect } from 'react';
import './styles/Mesero.css'

function Mesero() {
    const [pedidos, setPedidos] = useState([]);

    useEffect(() => {
        fetchPedidos();
    }, []);

    const fetchPedidos = async () => {
        try {
            const response = await fetch('http://localhost:5000/pedidos');
            if (response.ok) {
                const data = await response.json();
                setPedidos(data);
            } else {
                console.error('Error al obtener pedidos:', response.statusText);
            }
        } catch (error) {
            console.error('Error al obtener pedidos:', error);
        }
    };

    return (
        <div>
            <h2>Pedidos</h2>
            <table className='table'>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Estado</th>
                        <th>Mesa</th>
                        <th>Mesero</th>
                        <th>Pedido</th>
                    </tr>
                </thead>
                <tbody>
                    {pedidos.map(pedido => (
                        <tr key={pedido.id}>
                            <td>{pedido.id}</td>
                            <td>{pedido.estado}</td>
                            <td>{pedido.mesa}</td>
                            <td>{pedido.mesero}</td>
                            <td>
                                <ul>
                                    {pedido.pedido.map(item => (
                                        <li key={item.producto}>
                                            {item.cantidad} x {item.producto}
                                        </li>
                                    ))}
                                </ul>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Mesero;
