import React, { useState, useNavigate } from 'react';
import './App.css';
import logo from './logo.png';
function App() {
const navigate = useNavigate();
const [username, setUsername] = useState('');
const [password, setPassword] = useState('');
const [message, setMessage] = useState('');
const handleSubmit = async (e) => {
e.preventDefault();
try {
const response = await fetch('http://localhost:5000/login', {
method: 'POST',
headers: {
'Content-Type': 'application/json',
},
body: JSON.stringify({ username, password }),
});
const data = await response.json();
setMessage(data.message);
if (data.user && data.user.role === 'admin')
navigate('http://localhost:5000/admin-profile'); // Redirigir al usuario a la p치gina de AdminProfile
} catch (error) {
console.error('Error:', error);
}
};
return (
<div className="App">
<div className="form-container">
<div className="logo">
<img src={logo} alt="Logo" />
</div>
<h1>LOGIN</h1>
<div>
<input type="text" placeholder="Nombre de usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
</div>
<div>
<input type="password" placeholder="Contrase침a" value={password} onChange={(e) => setPassword(e.target.value)} />
</div>
<button onClick={handleSubmit}>Iniciar sesi칩n</button>
<p className="message">{message}</p>
</div>
</div>
);
}
export default App;
