import React, { useState, useEffect } from 'react';

import './styles/Admin.css';

function VentasProfile() {
  const [ventas, setVentas] = useState(null);
  const [activeTab, setActiveTab] = useState('user');

  
  useEffect(() => {
    fetchUsers();
}, []);

const fetchUsers = async () => {
    try {
        const response = await fetch('http://localhost:5000/ventas');
        if (response.ok) {
            const data = await response.json();
            setUsers(data);
        } else {
            console.error('Error al cargar la lista de usuarios');
        }
    } catch (error) {
        console.error('Error al cargar la lista de usuarios:', error);
    }
};

const handleVolver = () => {
    goTo('/administrador');
};


return (
    <div className="user-list">
        <button onClick={handleVolver}>Volver</button>
        <h2 className='titulo'>VENTAS</h2>
        <ul>
            <li className="list-header">
                <span>MESERO</span>
                <span>PRODUCTOS</span>
                <span>TOTAL</span>
            </li>
            {users.map(user => (
                <li key={user.username}>
                    <span>{user.username}</span>
                    <span>{user.password}</span>
                    <span>{user.role}</span>
                    <span>
                        <button onClick={() => handleEditUser(user.username)}>Editar</button>
                        <button onClick={() => handleDeleteUser(user.username)}>Eliminar</button>

                    </span>
                </li>
            ))}
        </ul>
    </div>
);
}
export default VentasProfile;
