import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/ListaUsuarios.css';

function AdminProfile() {
    const [users, setUsers] = useState([]);
    const goTo = useNavigate();


    useEffect(() => {
        // Función para cargar la lista de usuarios
        const fetchUsers = async () => {
          try {
            const response = await fetch('http://localhost:5000/users');
            if (response.ok) {
              const data = await response.json();
              setUsers(data);
            } else {
              console.error('Error al obtener la lista de usuarios');
            }
          } catch (error) {
            console.error('Error al obtener la lista de usuarios:', error);
          }
        };
    
        // Llamar a la función para cargar la lista de usuarios al montar el componente
        fetchUsers();
      }, []);


    const handleEditUser = (userId) => {
        // Implementa la lógica para editar el usuario con el ID proporcionado
        console.log('Editar usuario:', userId);
    };

    const handleDeleteUser = async (userId) => {
        
        try {
            const response = await fetch(`http://localhost:5000/users/${userId}`, {
              method: 'DELETE',
            });
            if (response.ok) {
              console.log('Usuario eliminado correctamente');
            fetchUsers()
            } else {
              console.error('Error al eliminar usuario');
            }
          } catch (error) {
            console.error('Error al eliminar usuario:', error);
          }
        };
        
    
    const handleVolver = () => {
        goTo('/administrador');
    };
    return (
        <div className="user-list">
            <button onClick={handleVolver}>Volver</button>
            <h2 className='titulo'>USUARIOS</h2>
            

            <ul>
                <li className="list-header">
                    <span>USUARIO</span>
                    <span>CONTRASEÑA</span>
                    <span>ROLE</span>
                    <span>ACCION</span>
                </li>
                {users.map(user => (
                    <li key={user.id}>
                        <span>{user.username}</span>
                        <span>{user.password}</span>
                        <span>{user.role}</span>
                        <span>
                            <button onClick={() => handleEditUser(user.id)}>Editar</button>
                            <button onClick={() => handleDeleteUser(user.id)}>Eliminar</button>

                        </span>
                    </li>
                ))}
            </ul>


        </div>


    );

}

export default AdminProfile;
