// App.js
import React, { useState } from 'react';
import './App.css';
import AdminProfile from './AdminProfile'; // Importa el componente AdminProfile

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Nuevo estado para controlar el estado del inicio de sesión

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      setMessage(data.message);

      if (data.user.role === 'admin') {
        setIsLoggedIn(true); // Establece isLoggedIn como true si el usuario es admin
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="App">
      {isLoggedIn ? ( // Si el usuario está logueado, muestra la página de perfil del administrador
        <AdminProfile />
      ) : (
        <form onSubmit={handleSubmit}>
          <label>
            Username:
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
          </label>
          <label>
            Password:
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </label>
          <button type="submit">Login</button>
        </form>
      )}
      <p>{message}</p>
    </div>
  );
}

export default App;
